interface Book {
    title: string;
    author: string;
    yearPublished: number;
    description: () => string | undefined;
    img:string;
}

function getBookDescription(): string | undefined {
    try {
        return `Title by Author ${this.author}`;
    } catch (error) {
        console.error(error);
        return undefined;
    }
}


