const booksDiv = document.querySelector('#books'); //same as getElementById('books')
console.dir(booksDiv)

function getFullBookDiv(book:Book): string{
    try {
        if(!book) throw new Error("the book is null");
        return `<div class='book' >
            <div class='book__img'><img src=${book.img} alt='some image'></img></div>
            <div class='book__info'>
                <h1>${book.title}</h1>
                <p>${book.description()} from ${book.yearPublished}</p>
            </div>
        </div>`
        
    } catch (error) {
        console.error(error);
        return 'undefined';
    }
}


function getDomDivElement(books:Book[]):string {
    try {
        var html = '';
        books.forEach(book => {
            html += getFullBookDiv(book);
        })
        return html;
    } catch (error) {
        console.error(error);
        return '';
    }
}

const html = getDomDivElement(books);
booksDiv?.innerHTML = html;
